import streamlit as stst.title("Homework 4")st.markdown('[Olivia Lin](https://github.com/linolivia), 19621212', unsafe_allow_html=True)ourfile = st.file_uploader("upload a file", type= 'csv')import pandas as pdimport numpy as npif ourfile is not None:      df = pd.read_csv(ourfile)    df = df.applymap(lambda x: np.nan if x == " " else x)        def can_be_number (c):        try:            pd.to_numeric(df[c])            return True        except:            return False        good_cols = [c for c in df.columns if can_be_number(c)]    df[good_cols] = df[good_cols].apply(pd.to_numeric, axis = 0)    df2 = df.copy()        x_axis = st.selectbox("select an x value", good_cols)    y_axis = st.selectbox("select a y value", good_cols)        our_range = st.slider("choose a range", 0, len(df["Index"])-1, (0,len(df["Index"])-1))        import altair as alt       st.write(f"You graph will range rows {our_range}, whose x-axis will be on {x_axis} and y-axis on {y_axis}.")    st.altair_chart(alt.Chart(df2[our_range[0]:our_range[1]]).mark_circle().encode(        x = x_axis,        y = y_axis,        tooltip = ["Song Name"]        ), use_container_width = True)